"use client";

import * as React from "react";
import { Lock, Trophy } from "lucide-react";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

// Types (inlined)
interface Achievement {
  id: string;
  name: string;
  trigger: "metric" | "api" | "streak";
  description?: string | null;
  badgeUrl?: string | null;
  key?: string;
  metricId?: string;
  metricName?: string;
  metricValue?: number;
  streakLength?: number;
  userAttributes?: Array<{ key: string; value: string }>;
  eventAttribute?: { key: string; value: string };
}

interface UserAchievement extends Achievement {
  achievedAt: string | null; // null = locked
}

// Variants
const achievementBadgeVariants = cva(
  "relative flex flex-col items-center gap-2 rounded-lg border p-4 transition-all",
  {
    variants: {
      variant: {
        card: "bg-card shadow-sm hover:shadow-md",
        badge: "bg-transparent border-none p-2",
        minimal: "border-none bg-transparent p-1",
      },
      size: {
        sm: "w-20",
        default: "w-28",
        lg: "w-36",
        xl: "w-44",
      },
    },
    defaultVariants: {
      variant: "card",
      size: "default",
    },
  },
);

// Props
interface AchievementBadgeProps
  extends
    React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof achievementBadgeVariants> {
  /** Achievement data (use UserAchievement for locked/unlocked state) */
  achievement: UserAchievement;
  /** Show description below name */
  showDescription?: boolean;
  /** Show progress for metric achievements */
  showProgress?: boolean;
  /** Current value for metric progress */
  currentValue?: number;
  /** Style for locked achievements */
  lockedStyle?: "grayscale" | "silhouette" | "hidden";
  /** Show lock icon on locked achievements */
  showLockIcon?: boolean;
  /** Custom icon when no badgeUrl */
  icon?: React.ReactNode;
}

const AchievementBadge = React.forwardRef<
  HTMLDivElement,
  AchievementBadgeProps
>(
  (
    {
      className,
      variant,
      size,
      achievement,
      showDescription = false,
      showProgress = false,
      currentValue = 0,
      lockedStyle = "grayscale",
      showLockIcon = true,
      icon,
      onClick,
      ...props
    },
    ref,
  ) => {
    const isUnlocked = achievement.achievedAt !== null;
    const isMetric = achievement.trigger === "metric";
    const target = achievement.metricValue ?? 0;
    const progress =
      target > 0 ? Math.min((currentValue / target) * 100, 100) : 0;

    // Don't render if locked and lockedStyle is "hidden"
    if (!isUnlocked && lockedStyle === "hidden") {
      return null;
    }

    const imageSize = {
      sm: "h-12 w-12",
      default: "h-16 w-16",
      lg: "h-20 w-20",
      xl: "h-24 w-24",
    }[size ?? "default"];

    const iconSize = {
      sm: "h-8 w-8",
      default: "h-10 w-10",
      lg: "h-12 w-12",
      xl: "h-14 w-14",
    }[size ?? "default"];

    const textSize = {
      sm: "text-xs",
      default: "text-sm",
      lg: "text-base",
      xl: "text-lg",
    }[size ?? "default"];

    return (
      <div
        ref={ref}
        role={onClick ? "button" : undefined}
        tabIndex={onClick ? 0 : undefined}
        onClick={onClick}
        onKeyDown={
          onClick
            ? (e) => {
                if (e.key === "Enter" || e.key === " ") {
                  e.preventDefault();
                  onClick(e as unknown as React.MouseEvent<HTMLDivElement>);
                }
              }
            : undefined
        }
        className={cn(
          achievementBadgeVariants({ variant, size }),
          onClick && "cursor-pointer",
          !isUnlocked && lockedStyle === "grayscale" && "opacity-50",
          className,
        )}
        {...props}
      >
        {/* Badge image or icon */}
        <div className="relative">
          {achievement.badgeUrl ? (
            <img
              src={achievement.badgeUrl}
              alt={achievement.name}
              className={cn(
                imageSize,
                "rounded-full object-cover",
                !isUnlocked && lockedStyle === "grayscale" && "grayscale",
                !isUnlocked &&
                  lockedStyle === "silhouette" &&
                  "brightness-0 opacity-30",
              )}
            />
          ) : (
            <div
              className={cn(
                imageSize,
                "flex items-center justify-center rounded-full",
                isUnlocked
                  ? "bg-purple-500 text-white"
                  : "bg-muted text-muted-foreground",
              )}
            >
              {icon ?? <Trophy className={iconSize} />}
            </div>
          )}

          {/* Lock icon overlay */}
          {!isUnlocked && showLockIcon && (
            <div className="absolute -bottom-1 -right-1 rounded-full bg-background p-1 shadow-sm">
              <Lock className="h-3 w-3 text-muted-foreground" />
            </div>
          )}
        </div>

        {/* Name */}
        <span
          className={cn(
            textSize,
            "text-center font-medium leading-tight",
            !isUnlocked && "text-muted-foreground",
          )}
        >
          {achievement.name}
        </span>

        {/* Description */}
        {showDescription && achievement.description && (
          <span className="text-xs text-center text-muted-foreground line-clamp-2">
            {achievement.description}
          </span>
        )}

        {/* Progress bar for metric achievements */}
        {showProgress && isMetric && !isUnlocked && (
          <div className="w-full space-y-1">
            <div className="h-1.5 w-full overflow-hidden rounded-full bg-muted">
              <div
                className="h-full bg-purple-500 transition-all"
                style={{ width: `${progress}%` }}
              />
            </div>
            <span className="text-xs text-muted-foreground text-center block">
              {currentValue}/{target}
            </span>
          </div>
        )}
      </div>
    );
  },
);
AchievementBadge.displayName = "AchievementBadge";

export { AchievementBadge, achievementBadgeVariants };
export type { AchievementBadgeProps, Achievement, UserAchievement };

"use client";

import * as React from "react";
import { Trophy } from "lucide-react";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

// Types (inlined)
interface Achievement {
  id: string;
  name: string;
  trigger: "metric" | "api" | "streak";
  description?: string | null;
  badgeUrl?: string | null;
  key?: string;
  metricId?: string;
  metricName?: string;
  metricValue?: number;
  streakLength?: number;
}

interface UserAchievement extends Achievement {
  achievedAt: string | null; // null = locked
}

// Variants
const achievementGridVariants = cva("grid", {
  variants: {
    columns: {
      2: "grid-cols-2",
      3: "grid-cols-3",
      4: "grid-cols-4",
      auto: "grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5",
    },
    gap: {
      sm: "gap-2",
      default: "gap-4",
      lg: "gap-6",
    },
  },
  defaultVariants: {
    columns: "auto",
    gap: "default",
  },
});

// Badge size mapping
const badgeSizeMap = {
  sm: "w-20",
  default: "w-28",
  lg: "w-36",
} as const;

// Props
interface AchievementGridProps
  extends
    React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof achievementGridVariants> {
  /** Array of achievements */
  achievements: UserAchievement[];
  /** Filter achievements */
  filter?: "all" | "earned" | "locked";
  /** Sort achievements */
  sortBy?: "name" | "earned" | "trigger";
  /** Size of achievement badges */
  badgeSize?: "sm" | "default" | "lg";
  /** Variant for badges */
  badgeVariant?: "card" | "badge" | "minimal";
  /** Style for locked achievements */
  lockedStyle?: "grayscale" | "silhouette" | "hidden";
  /** Custom empty state */
  emptyState?: React.ReactNode;
  /** Callback when achievement clicked */
  onAchievementClick?: (achievement: UserAchievement) => void;
}

const AchievementGrid = React.forwardRef<HTMLDivElement, AchievementGridProps>(
  (
    {
      className,
      columns,
      gap,
      achievements,
      filter = "all",
      sortBy = "name",
      badgeSize = "default",
      badgeVariant = "card",
      lockedStyle = "grayscale",
      emptyState,
      onAchievementClick,
      ...props
    },
    ref,
  ) => {
    // Filter achievements
    const filtered = React.useMemo(() => {
      switch (filter) {
        case "earned":
          return achievements.filter((a) => a.achievedAt !== null);
        case "locked":
          return achievements.filter((a) => a.achievedAt === null);
        default:
          return achievements;
      }
    }, [achievements, filter]);

    // Sort achievements
    const sorted = React.useMemo(() => {
      const copy = [...filtered];
      switch (sortBy) {
        case "earned":
          return copy.sort((a, b) => {
            if (a.achievedAt && b.achievedAt) {
              return (
                new Date(b.achievedAt).getTime() -
                new Date(a.achievedAt).getTime()
              );
            }
            if (a.achievedAt) return -1;
            if (b.achievedAt) return 1;
            return 0;
          });
        case "trigger":
          return copy.sort((a, b) => a.trigger.localeCompare(b.trigger));
        case "name":
        default:
          return copy.sort((a, b) => a.name.localeCompare(b.name));
      }
    }, [filtered, sortBy]);

    // Empty state
    if (sorted.length === 0) {
      if (emptyState) {
        return <>{emptyState}</>;
      }
      return (
        <div className="flex flex-col items-center justify-center py-12 text-center text-muted-foreground">
          <Trophy className="h-12 w-12 mb-4 opacity-50" />
          <p className="text-sm">
            {filter === "earned"
              ? "No achievements earned yet"
              : filter === "locked"
                ? "All achievements unlocked!"
                : "No achievements available"}
          </p>
        </div>
      );
    }

    const imageSize = {
      sm: "h-12 w-12",
      default: "h-16 w-16",
      lg: "h-20 w-20",
    }[badgeSize];

    const iconSize = {
      sm: "h-8 w-8",
      default: "h-10 w-10",
      lg: "h-12 w-12",
    }[badgeSize];

    return (
      <div
        ref={ref}
        className={cn(achievementGridVariants({ columns, gap }), className)}
        {...props}
      >
        {sorted.map((achievement) => {
          const isUnlocked = achievement.achievedAt !== null;

          // Skip if hidden and locked
          if (!isUnlocked && lockedStyle === "hidden") {
            return null;
          }

          return (
            <div
              key={achievement.id}
              role={onAchievementClick ? "button" : undefined}
              tabIndex={onAchievementClick ? 0 : undefined}
              onClick={() => onAchievementClick?.(achievement)}
              onKeyDown={
                onAchievementClick
                  ? (e) => {
                      if (e.key === "Enter" || e.key === " ") {
                        e.preventDefault();
                        onAchievementClick(achievement);
                      }
                    }
                  : undefined
              }
              className={cn(
                "flex flex-col items-center gap-2 rounded-lg p-4 transition-all",
                badgeVariant === "card" &&
                  "border bg-card shadow-sm hover:shadow-md",
                badgeVariant === "badge" && "bg-transparent",
                badgeVariant === "minimal" && "bg-transparent p-2",
                onAchievementClick && "cursor-pointer",
                !isUnlocked && lockedStyle === "grayscale" && "opacity-50",
              )}
            >
              {/* Badge image */}
              {achievement.badgeUrl ? (
                <img
                  src={achievement.badgeUrl}
                  alt={achievement.name}
                  className={cn(
                    imageSize,
                    "rounded-full object-cover",
                    !isUnlocked && lockedStyle === "grayscale" && "grayscale",
                    !isUnlocked &&
                      lockedStyle === "silhouette" &&
                      "brightness-0 opacity-30",
                  )}
                />
              ) : (
                <div
                  className={cn(
                    imageSize,
                    "flex items-center justify-center rounded-full",
                    isUnlocked
                      ? "bg-purple-500 text-white"
                      : "bg-muted text-muted-foreground",
                  )}
                >
                  <Trophy className={iconSize} />
                </div>
              )}

              {/* Name */}
              <span
                className={cn(
                  "text-sm text-center font-medium leading-tight",
                  !isUnlocked && "text-muted-foreground",
                )}
              >
                {achievement.name}
              </span>
            </div>
          );
        })}
      </div>
    );
  },
);
AchievementGrid.displayName = "AchievementGrid";

export { AchievementGrid, achievementGridVariants };
export type { AchievementGridProps, Achievement, UserAchievement };

"use client";

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

// Types (inlined)
interface Achievement {
  id: string;
  name: string;
  trigger: "metric" | "api" | "streak";
  description?: string | null;
  badgeUrl?: string | null;
  key?: string;
  metricId?: string;
  metricName?: string;
  metricValue?: number;
  streakLength?: number;
}

// Variants
const achievementProgressVariants = cva("w-full", {
  variants: {
    variant: {
      default: "",
      gradient: "",
    },
    size: {
      sm: "space-y-1",
      default: "space-y-1.5",
      lg: "space-y-2",
    },
  },
  defaultVariants: {
    variant: "default",
    size: "default",
  },
});

const progressBarVariants = cva(
  "h-full rounded-full transition-all duration-500 ease-out",
  {
    variants: {
      variant: {
        default: "bg-purple-500",
        gradient: "bg-gradient-to-r from-purple-500 to-pink-500",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
);

// Props
interface AchievementProgressProps
  extends
    React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof achievementProgressVariants> {
  /** Achievement data (must be metric type with metricValue) */
  achievement: Achievement;
  /** Current progress value */
  currentValue: number;
  /** Show numeric value (e.g., "45/100") */
  showValue?: boolean;
  /** Show percentage */
  showPercentage?: boolean;
  /** Show achievement name */
  showName?: boolean;
  /** Animate the progress bar */
  animate?: boolean;
}

const AchievementProgress = React.forwardRef<
  HTMLDivElement,
  AchievementProgressProps
>(
  (
    {
      className,
      variant,
      size,
      achievement,
      currentValue,
      showValue = true,
      showPercentage = false,
      showName = false,
      animate = true,
      ...props
    },
    ref,
  ) => {
    const target = achievement.metricValue ?? 0;
    const progress =
      target > 0 ? Math.min((currentValue / target) * 100, 100) : 0;
    const isComplete = currentValue >= target;

    const barHeight = {
      sm: "h-1.5",
      default: "h-2",
      lg: "h-3",
    }[size ?? "default"];

    const textSize = {
      sm: "text-xs",
      default: "text-sm",
      lg: "text-base",
    }[size ?? "default"];

    // For animation, start at 0 and animate to actual value
    const [displayProgress, setDisplayProgress] = React.useState(
      animate ? 0 : progress,
    );

    React.useEffect(() => {
      if (animate) {
        // Small delay to ensure the animation is visible
        const timeout = setTimeout(() => {
          setDisplayProgress(progress);
        }, 100);
        return () => clearTimeout(timeout);
      } else {
        setDisplayProgress(progress);
      }
    }, [progress, animate]);

    return (
      <div
        ref={ref}
        className={cn(
          achievementProgressVariants({ variant, size }),
          className,
        )}
        {...props}
      >
        {/* Header row */}
        {(showName || showValue || showPercentage) && (
          <div className={cn("flex items-center justify-between", textSize)}>
            {showName && (
              <span className="font-medium truncate mr-2">
                {achievement.name}
              </span>
            )}
            <div className="flex items-center gap-2 ml-auto text-muted-foreground">
              {showValue && (
                <span>
                  {currentValue}/{target}
                </span>
              )}
              {showPercentage && <span>{Math.round(progress)}%</span>}
            </div>
          </div>
        )}

        {/* Progress bar */}
        <div
          className={cn(
            "w-full overflow-hidden rounded-full bg-muted",
            barHeight,
          )}
          role="progressbar"
          aria-valuenow={currentValue}
          aria-valuemin={0}
          aria-valuemax={target}
          aria-label={`${achievement.name} progress`}
        >
          <div
            className={cn(
              progressBarVariants({ variant }),
              isComplete && "bg-green-500",
            )}
            style={{ width: `${displayProgress}%` }}
          />
        </div>

        {/* Completion message */}
        {isComplete && (
          <p className={cn("text-green-600 dark:text-green-400", textSize)}>
            Complete! ðŸŽ‰
          </p>
        )}
      </div>
    );
  },
);
AchievementProgress.displayName = "AchievementProgress";

export { AchievementProgress, achievementProgressVariants };
export type { AchievementProgressProps, Achievement };

"use client";

import * as React from "react";
import { Share2, Trophy, X } from "lucide-react";

import { cn } from "@/lib/utils";

// Types (inlined)
interface Achievement {
  id: string;
  name: string;
  trigger: "metric" | "api" | "streak";
  description?: string | null;
  badgeUrl?: string | null;
  key?: string;
  metricId?: string;
  metricName?: string;
  metricValue?: number;
  streakLength?: number;
}

// Props
interface AchievementUnlockedProps {
  /** Achievement that was unlocked */
  achievement: Achievement;
  /** Control open state */
  open: boolean;
  /** Callback when open state changes */
  onOpenChange: (open: boolean) => void;
  /** Callback when share button clicked */
  onShare?: () => void;
  /** Custom class for the dialog */
  className?: string;
}

const AchievementUnlocked = React.forwardRef<
  HTMLDivElement,
  AchievementUnlockedProps
>(({ achievement, open, onOpenChange, onShare, className }, ref) => {
  // Handle escape key
  React.useEffect(() => {
    if (!open) return;

    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === "Escape") {
        onOpenChange(false);
      }
    };

    document.addEventListener("keydown", handleEscape);
    return () => document.removeEventListener("keydown", handleEscape);
  }, [open, onOpenChange]);

  // Prevent body scroll when open
  React.useEffect(() => {
    if (open) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
    }
    return () => {
      document.body.style.overflow = "";
    };
  }, [open]);

  if (!open) return null;

  return (
    <>
      {/* Backdrop */}
      <div
        className="fixed inset-0 z-50 bg-black/80 motion-safe:animate-in motion-safe:fade-in-0"
        onClick={() => onOpenChange(false)}
        aria-hidden="true"
      />

      {/* Dialog */}
      <div
        ref={ref}
        role="dialog"
        aria-modal="true"
        aria-labelledby="achievement-title"
        className={cn(
          "fixed left-1/2 top-1/2 z-50 -translate-x-1/2 -translate-y-1/2",
          "w-full max-w-md rounded-xl bg-card p-6 shadow-2xl",
          "motion-safe:animate-in motion-safe:fade-in-0 motion-safe:zoom-in-95",
          className,
        )}
      >
        {/* Close button */}
        <button
          type="button"
          onClick={() => onOpenChange(false)}
          className="absolute right-4 top-4 rounded-sm opacity-70 ring-offset-background transition-opacity hover:opacity-100 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2"
          aria-label="Close"
        >
          <X className="h-4 w-4" />
        </button>

        {/* Content */}
        <div className="flex flex-col items-center text-center">
          {/* Celebration text */}
          <span className="mb-4 text-sm font-medium uppercase tracking-wider text-purple-500">
            Achievement Unlocked!
          </span>

          {/* Badge */}
          <div className="relative mb-6">
            {/* Glow effect */}
            <div className="absolute inset-0 rounded-full bg-purple-500/30 blur-xl motion-safe:animate-pulse" />

            {achievement.badgeUrl ? (
              <img
                src={achievement.badgeUrl}
                alt={achievement.name}
                className="relative h-32 w-32 rounded-full object-cover ring-4 ring-purple-500/50"
              />
            ) : (
              <div className="relative flex h-32 w-32 items-center justify-center rounded-full bg-purple-500 text-white ring-4 ring-purple-500/50">
                <Trophy className="h-16 w-16" />
              </div>
            )}
          </div>

          {/* Name */}
          <h2 id="achievement-title" className="mb-2 text-2xl font-bold">
            {achievement.name}
          </h2>

          {/* Description */}
          {achievement.description && (
            <p className="mb-6 text-muted-foreground">
              {achievement.description}
            </p>
          )}

          {/* Actions */}
          <div className="flex gap-3">
            <button
              type="button"
              onClick={() => onOpenChange(false)}
              className="rounded-lg bg-primary px-6 py-2.5 font-medium text-primary-foreground transition-colors hover:bg-primary/90"
            >
              Awesome!
            </button>

            {onShare && (
              <button
                type="button"
                onClick={onShare}
                className="flex items-center gap-2 rounded-lg border px-4 py-2.5 font-medium transition-colors hover:bg-muted"
              >
                <Share2 className="h-4 w-4" />
                Share
              </button>
            )}
          </div>
        </div>
      </div>
    </>
  );
});
AchievementUnlocked.displayName = "AchievementUnlocked";

export { AchievementUnlocked };
export type { AchievementUnlockedProps, Achievement };

"use client"

import * as React from "react"
import { Crown, Medal, Trophy, TrendingDown, TrendingUp } from "lucide-react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

// Types (inlined)
interface LeaderboardRanking {
  userId: string
  userName: string | null
  rank: number
  value: number
}

interface LeaderboardResponse {
  id: string
  name: string
  key: string
  rankBy: "points" | "streak" | "metric"
  status: string
  description: string | null
  rankings: LeaderboardRanking[]
  start: string
  end: string | null
  maxParticipants: number
}

// Variants
const leaderboardVariants = cva("w-full", {
  variants: {
    variant: {
      table: "divide-y divide-border",
      cards: "space-y-2",
      compact: "divide-y divide-border text-sm",
    },
  },
  defaultVariants: {
    variant: "table",
  },
})

// Helper to get rank icon/medal
function getRankDisplay(rank: number) {
  switch (rank) {
    case 1:
      return { icon: Crown, color: "text-yellow-500", bg: "bg-yellow-500/10" }
    case 2:
      return { icon: Medal, color: "text-gray-400", bg: "bg-gray-400/10" }
    case 3:
      return { icon: Medal, color: "text-amber-600", bg: "bg-amber-600/10" }
    default:
      return null
  }
}

// Props
interface LeaderboardProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof leaderboardVariants> {
  /** Full leaderboard response from Trophy API */
  leaderboard?: LeaderboardResponse
  /** Or just provide rankings directly */
  rankings?: LeaderboardRanking[]
  /** Highlight current user */
  currentUserId?: string
  /** Max entries to show */
  limit?: number
  /** Show value column */
  showValue?: boolean
  /** Label for value column */
  valueLabel?: string
  /** Show avatar placeholder */
  showAvatar?: boolean
  /** Custom empty state */
  emptyState?: React.ReactNode
  /** Callback when user row clicked */
  onUserClick?: (ranking: LeaderboardRanking) => void
}

const Leaderboard = React.forwardRef<HTMLDivElement, LeaderboardProps>(
